import React, {Fragment, useState, useEffect} from 'react';
import './css/bootstrap.min.css';
import './css/font-awesome.min.css';
import './css/layers.css';
import './css/navigation.css';
import './css/owl.carousel.min.css';
import './css/responsive.css';
import './css/style.css';

import Header from './components/header';

function App() {
    // fetch("https://coronavirus-tracker-api.herokuapp.com/v2/locations?country=India").then((response) => {
    //     return response.json();
    //   })
    //   .then((data) => {
    //     console.log(data);
    //   });

    const [data, setData] = useState([]);

    async function fetchUrl() {
        //const response = await fetch("https://jsonplaceholder.typicode.com/photos?albumId=1");
        const response = await fetch("https://coronavirus-tracker-api.herokuapp.com/v2/locations");
        const json = await response.json();
        console.log(json.locations);
        setData(json.locations);
      }

    useEffect(() => {
        fetchUrl();
      }, []
    );

  return (
    <Fragment>
        <select>
        {
            data.map(({ id, country}) => (
                <option key={id}>{country}</option>
              ))
        }
        </select>

    </Fragment>
  );
}

export default App;
